#include <stdio.h>

// 

// 在1000位数中，连续读4位数字的最大乘积，滑动窗口法
int main()
{
  int ans = 0, count = 0;

  int str[1005];
  scanf("%s", str);
  long long now = 1;

  for (int i = 0; i < 1000; i++)
  {
    // 求13位数最大的积，所以直接乘
    if (i < 13)
    {
      // 字符串转数字
      now *= str[i] - '0';
    }
    else
    {
      // 待乘数检查
      if (str[i] == '0')
      {
        count++;
      }
      else
      {
        now *= str[i] - '0';
      }

      // 待除数检查
      if (str[i - 13] == '0')
      {
        count--;
      }
      else
      {
        now /= str[i - 13] - '0';
      }
    }
    // 没有零 才更新
    if (count == 0 && ans < now)
    {
      ans = now;
    }
  }

  printf(" %ld \n", ans);
  return 0;
}